export declare const styles = "\n  :host {\n    display: inline-block;\n  }\n\n  .aui-avatar {\n    display: inline-flex;\n    align-items: center;\n    justify-content: center;\n    overflow: hidden;\n    background-color: var(--aui-color-primary-100, #e3f2fd);\n    color: var(--aui-color-primary-700, #1976d2);\n    font-weight: 500;\n    user-select: none;\n  }\n\n  /* Variants */\n  .variant-circular {\n    border-radius: 50%;\n  }\n\n  .variant-rounded {\n    border-radius: var(--aui-border-radius-md, 8px);\n  }\n\n  .variant-square {\n    border-radius: 0;\n  }\n\n  /* Sizes */\n  .size-small {\n    width: 32px;\n    height: 32px;\n    font-size: 0.875rem;\n  }\n\n  .size-medium {\n    width: 40px;\n    height: 40px;\n    font-size: 1rem;\n  }\n\n  .size-large {\n    width: 56px;\n    height: 56px;\n    font-size: 1.25rem;\n  }\n\n  .size-xlarge {\n    width: 72px;\n    height: 72px;\n    font-size: 1.5rem;\n  }\n\n  /* Content */\n  .avatar-image {\n    width: 100%;\n    height: 100%;\n    object-fit: cover;\n  }\n\n  .avatar-initials {\n    text-transform: uppercase;\n  }\n\n  .avatar-placeholder svg {\n    width: 60%;\n    height: 60%;\n  }\n";
